CREATE TABLE AttackStrikes (Id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, Attack_Id INTEGER NOT NULL REFERENCES Attacks (Id), AttackGeometry_Id INTEGER REFERENCES AttackGeometries (Id), Animation_Id INTEGER REFERENCES Animations (Id), AnimationRequiresRotation BOOLEAN DEFAULT (0), TimestampMs INTEGER DEFAULT (0) NOT NULL, DiesAfterCollision BOOLEAN DEFAULT (0) NOT NULL, IsBlockable BOOLEAN NOT NULL, HeightOffset INTEGER DEFAULT (0) NOT NULL);
