CREATE TABLE ColorSchemeParts (Id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, ColorScheme_Id INTEGER REFERENCES ColorSchemes (Id) ON DELETE CASCADE NOT NULL, PartNumber INTEGER NOT NULL, Red INTEGER NOT NULL DEFAULT o CHECK ((Red >= 0 AND Red <= 255)), Green INTEGER NOT NULL DEFAULT (0) CHECK ((Green >= 0 AND Green <= 255)), Blue INTEGER NOT NULL CHECK ((Blue >= 0 AND Blue <= 255)) DEFAULT (0), Alpha INTEGER DEFAULT (255) CHECK ((Alpha >= 0 AND Alpha <= 255)) NOT NULL);
