CREATE TABLE WeaponProgressTracks (Id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, Level INTEGER NOT NULL CHECK ((Level >= 1 AND Level <= 7)), CompletionAmount INTEGER NOT NULL, WeaponType_Enum TEXT NOT NULL, BonusDamageMultiplier DOUBLE NOT NULL DEFAULT (1), BonusToMinDamage INTEGER NOT NULL DEFAULT (0), BonusToMaxDamage INTEGER NOT NULL DEFAULT (0));
